import React from 'react';
import './../assets/about.scss';
import mountain0 from '../assets/img/mountains/0.png'
import mountain1 from '../assets/img/mountains/1.png'
import mountain2 from '../assets/img/mountains/2.png'
import mountain3 from '../assets/img/mountains/3.png'
import mountain4 from '../assets/img/mountains/4.jpg'

export default class About extends React.Component{
    // elem.style.webkitTransform="translate3d(0,-20px,0)"
    componentDidMount () {
        console.log('componentDidMount')
        window.addEventListener('scroll', this.handleParallax)
    }
    componentWillUnmount () {
        console.log('componentWillUnmount')
        window.removeEventListener('scroll', this.handleParallax)
    }
    handleParallax () {
        let mountain0Scroll = window.scrollY / 6
        let mountain1Scroll = window.scrollY / 4
        document.getElementById('mountain0').style.webkitTransform=`translate3d(0,${-600 + mountain0Scroll}px,0)`
        document.getElementById('mountain1').style.webkitTransform=`translate3d(0,${-1200 + mountain1Scroll}px,0)`
        document.getElementById('mountain2').style.webkitTransform=`translate3d(0,${-1200 + mountain1Scroll}px,0)`
    }
    render(){
        return (
            <div>
                <div className="container">
                    <div id="sky">
                        <img src={mountain4} alt="sky"/>
                    </div>
                    <div id="mountain0">
                        <img src={mountain0} alt="mountain0"/>
                    </div>
                    <div id="mountain1">
                        <img src={mountain1} alt="mountain1"/>
                    </div>
                    <div id="mountain2">
                        <img src={mountain2} alt="mountain2"/>
                    </div>
                </div>
            </div>
        )
    }
}
